import React from 'react'

class EventCreate extends React.Component {
    id = React.createRef()
    event_type = React.createRef()
    event_name = React.createRef()
    release_year = React.createRef()
    charges = React.createRef()
    state = {msg: ''}
    addEmployee() {
        var url = 'http://localhost:3001/employees/'
        var emp = {
            id: Number(this.id.current.value),
            event_type: this.event_type.current.value,
            event_name: this.event_name.current.value,
            release_year: this.release_year.current.value,
            charges: this.charges.current.value
        }
        fetch(url, {
            method: 'POST',
            body: JSON.stringify(emp),
            headers: {"content-type": "application/json"}
        })
        .then(response => this.setState({msg: 'Record Added Successfully...........'}))
        .catch(error => this.setState({msg: error}))
    }
    render () {
        return <div>
            <h1>Add new Employee</h1>
            ID: <input type="number" ref={this.id}/> <br/>
            Employee Name: <input type="text" ref={this.ename}/> <br/>
            Job: <input type="text" ref={this.job}/> <br/>
            Salary: <input type="text" ref={this.salary}/> <br/>
            <button onClick = {() => this.addEmployee()}>Add Employee</button> <hr/>
            {this.state.msg}
        </div>
    }
}

export default EventCreate
